mapper.select:

select 
	board_id, title, content, writer, createddate, updateddate
from
	(
		select /*+INDEX_DESC(tbl_board pk_board)*/
		rownum rn, board_id, title, content, writer, createddate, updateddate
		from tbl_board
		where rownum <= #{pageNum} * #{amount} 
	)
where rn > (#{pageNum} - 1) * #{amount}
