mapper.select:

select
	bno, isbn, title, publisher, thumbnail, url, datetime, userid
from
	(
		select /*+INDEX_DESC(tbl_book pk_book)*/
		rownum rn, bno, isbn, title, publisher, thumbnail, url, datetime, userid
		from tbl_book
		where rownum <= #{criteria.pageNum} * #{criteria.amount} and userid = #{userId}
	)
where rn > (#{criteria.pageNum} - 1) * #{criteria.amount}

select book.bno, book.isbn, title,  publisher, thumbnail, url, datetime, createdate, authors  
from tbl_book book inner join tbl_book_author author on book.bno = author.bno
where book.bno = #{bno}

select bno, isbn, title, publisher, thumbnail, url, datetime, createdate  
from tbl_book
where isbn = #{isbn}
